import { Fragment, useState, useEffect, Component } from "react";
import classes from "./UserFinder.module.css";
import UsersContext from '../store/users-context';

import Users from "./Users";

// const DUMMY_USERS = [
//   { id: "u1", name: "Max" },
//   { id: "u2", name: "Manuel" },
//   { id: "u3", name: "Julie" },
